import React, { Component, ScrollContainer } from 'react';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer, Brush } from 'recharts';
import Row from "react-bootstrap/esm/Row";
const data = [];

for (let num = 100; num >= 0; num--) {
    data.push({
        y: Math.floor(Math.random() * (400 - 180 + 1)) + 180

    });

}


class ECG extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            start: 0,
            end: 0,
            val: [],
            count: 0,
            news: [],

        };
    }

    componentDidMount = () => {
        let flag = 0;
        let i = 0;

        this.myTimer = setInterval(() => {
            flag = flag + 1;

            //if (i === 0) {
            //    this.setState({ news: data });
            //    i=1;
            //}

            if (flag === 100) {
               // this.setState({new : this.val })
                this.setState({ val: [] });
                flag = 0;
            }
                

            this.setState  ({
                val: [...this.state.val, Math.floor(Math.random() * (400 - 180 + 1)) + 180],
                
            });
            
        }, 1000);
    };


    componentWillMount = () => {
        clearInterval(this.myTimer);
    }

    

    
    render() {
        const { val } = this.state;

        return (
            
            <ResponsiveContainer width="100%" height="100%" >

                <LineChart width={300} height={80} data={data}>
                    <Line type="monotone" dataKey="y" stroke="#8884d8" strokeWidth={3} dot={false} />
                    <CartesianGrid opacity={1} vertical={false} />
                    {/*<Brush startIndex={this.state.start} endIndex={this.state.end} />*/}
                    <YAxis />
                </LineChart>
           
    </ResponsiveContainer >
                
        );
    }
}
export default ECG;
