import Row from "react-bootstrap/esm/Row";
const data = [];

for (let num = 6; num >= 0; num--) {
    data.push({
        y: Math.floor(Math.random() * (400 - 180 + 1)) + 180

    });

}


class ECG extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            start: 0,
            end: 0,
            val: [],
            count: 0,
            news: data,

        };
    }

    componentDidMount = () => {
        let flag = 0;
        let i = 0;

        this.myTimer = setInterval(() => {
            flag = flag + 1;

            if (i === 0) {
                this.setState({ news: data });
                i=1;
            }

            if (flag === 5) {
                this.setState({news : this.val })
                this.setState({ val: [] });
                flag = 0;
            }
                

            this.setState  ({
                val: [...this.state.val, {y: Math.floor(Math.random() * (400 - 180 + 1)) + 180 }],
                
            });
            
        }, 1000);
    };


    componentWillMount = () => {
        clearInterval(this.myTimer);
    }

    

    
    render() {
        const { val } = this.state;
        const { news } = this.state;

        return (
            <div>

                {news.map(({ y }) => {
                    return <p key={y}> {y} </p>
                })}

            </div>
